<ChangingProgressProvider values={[0, 20, 40, 60, 80, 100]}>
            {percentage => (
                <CircularProgressbar value={percentage} text={`${percentage}%`} />
            )}<CircularProgressbar
                value={percentage}
                text={`${percentage}%`}
                background
                backgroundPadding={6}
                styles={buildStyles({
                backgroundColor: "#3e98c7",
                textColor: "#fff",
                pathColor: "#fff",
                trailColor: "transparent"
                })}
            />
